// ðŸ‘ˆ ignorar simbolos como "&" "<>" !importante
//js ðŸ‘ˆ comando decorativo "Highlight String Code"

let index = 0;
let isPaused = false;
carusel = document.getElementById("carusel-cont")   
function actualizarIndexCarusel(contenedor = carusel) {
  let items = contenedor.querySelectorAll(".carusel-link");
  if (items.length = 0) return
  let scrollLeft = contenedor.scrollLeft;
  let closestIndex = 0;
  let minDiff = Math.abs(scrollLeft - items[0].offsetLeft);   
  items.forEach((item, i) => {
    let diff = Math.abs(scrollLeft - item.offsetLeft);
    if (diff < minDiff) {
      minDiff = diff;
      closestIndex = i;
    }
  });
  index = closestIndex;
}   
function moverCarusel(direccion = 'adelante', contenedor) {
    const items = contenedor.querySelectorAll(".carusel-link");
    if (items.length = 0) return
    actualizarIndexCarusel(contenedor); // Actualiza la posiciÃ³n antes de cambiar
    // Nueva posicion
    index = (direccion === "atras") 
        ? (index - 1 + items.length) % items.length  // Si es menor a 0, reinicia al Ãºltimo
        : (index + 1) % items.length;  // Si es mayor al Ãºltimo, vuelve a 0
    contenedor.scrollTo({
        left: items[index].offsetLeft, 
        behavior: "smooth"
    }); // scroll a la imagen indicada
}
function autoScrollCarusel() {
  if (isPaused) return; // Detener el movimiento si estÃ¡ en hover
  moverCarusel('adelante', carusel)
}
window.addEventListener("load", () => {
  setInterval(autoScrollCarusel, 5000); // Mueve cada 5 segundos
});

document.addEventListener("DOMContentLoaded", function () {
  // Detectar hover para pausar el movimiento (PC)
  carusel.addEventListener("mouseenter", () => isPaused = true);
  carusel.addEventListener("mouseleave", () => isPaused = false);   
  // Detectar toque en mÃ³viles para pausar el movimiento
  carusel.addEventListener("touchstart", () => isPaused = true);
  document.addEventListener("visibilitychange", () => {
    isPaused = document.hidden;
  });
});
