// aplica la libreria Fancybox a las imagenes del blog y pone los botones de descarga
function customImagenesPosts() {
  posts.forEach((post, index) => {
    post.querySelectorAll("img").forEach(img => {
      const parentImg = img.parentElement;
      if (parentImg.tagName !== "A") return // si el padre no es un <a></a>
      if (parentImg.href.startsWith("https://blogger.googleusercontent.com/img/")) { // si es una imagen de blogger
        parentImg.setAttribute("data-fancybox", "post-group-" + index);
        agregarBotonDescarga(img)
      } else if (parentImg.getAttribute("href").trim() == ""){
        parentImg.style.pointerEvents = "none"
      }
    });
  });
  Fancybox.bind("[data-fancybox]", {
    compact: true, //desactiva la vercion movil
    animated: true, //  animaciones
    panOnlyZoomed: true, // Permite hacer zoom incluso sin hacer clic
    on: {
      close: () => {
        // prevenir errores con lectores de pantalla
        if (document.activeElement) {
          document.activeElement.blur();
        }
      }
    }
  });
  document.documentElement.style.setProperty("--fancybox-zIndex", "18");
}
function agregarBotonDescarga(imagen) {
  imagen.insertAdjacentHTML('beforebegin', `
    <button class="download-btn" onclick="
      event.preventDefault(); 
      event.stopPropagation();
      descargarImagen('${imagen.parentElement.href}')
    ">
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" stroke="currentcolor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" class="icon icon-tabler icons-tabler-outline icon-tabler-photo-down" viewBox="0 0 24 24"><path stroke="none" d="M0 0h24v24H0z"/><path d="M15 8h0M12.5 21H6a3 3 0 0 1-3-3V6a3 3 0 0 1 3-3h12a3 3 0 0 1 3 3v6.5"/><path d="m3 16 5-5c1-.9 2-.9 3 0l4 4"/><path d="m14 14 1-1c.7-.6 1.4-.8 2.1-.6M19 16v6M22 19l-3 3-3-3"/></svg>
    </button>
  `);
}   
