// ðŸ‘ˆ ignorar simbolos como "&" "<>" !importante
//js ðŸ‘ˆ comando decorativo "Highlight String Code"
const menu = document.getElementById("sidebar-right");
const fondoOpaco = document.getElementById("overlay-fondo-opaco");
let lugarInicialX, lugarInicialY, distanciaX, menuDesplegado;

function toggleMenu(parametro = 'toggle'){
  switch (parametro) {
    case 'mostrar':
      menu.classList.remove('ocultarMenu');
      fondoOpaco.classList.add('visible');
      break;
    case 'ocultar':
      menu.classList.add('ocultarMenu');
      fondoOpaco.classList.remove('visible');
      break;
    case 'toggle':
      menu.classList.toggle('ocultarMenu');
      fondoOpaco.classList.toggle('visible');
      break;
  }
}

document.addEventListener("pointerdown", (event) => {
    lugarInicialX = event.clientX;
    lugarInicialY = event.clientY;
    distanciaX = window.innerWidth - lugarInicialX;
    menuDesplegado = !menu.classList.contains('ocultarMenu');

    //<!--^ cerrar al tocar fuera del menu ðŸ”’ --> 
    if (menuDesplegado && distanciaX > menu.offsetWidth) {
      toggleMenu('ocultar')
    }
});

document.addEventListener("touchmove", (event) => {
    // ignorar cuando esta lejos del borde âŒ
    if (distanciaX > 90 && !menuDesplegado) return
    let recoridoY = event.touches[0].clientY - lugarInicialY
    let recoridoX = event.touches[0].clientX - lugarInicialX;

    // ignorar si hay mucho Recorrido en "Y" âŒ
    if (recoridoY > 25 || recoridoY < -25) return;

    //<!-- ^ logica al abrir ðŸ”“--> 
    if (recoridoX < -20) {
        toggleMenu('mostrar')
    }
    //<!-- ^ logica al cerrar ðŸ”’--> 
    if (recoridoX > 20) {
        toggleMenu('ocultar')
    }
});

function agregar_Blank() {
  // Selecciona todos los enlaces dentro de widgets con clase "Image"
  const imageLinks = document.querySelectorAll(".widget.Image a");

  imageLinks.forEach(function (link) {
    // Verifica que el enlace tenga un href vÃ¡lido
    if (link.href && link.getAttribute("target") !== "_blank") {
      link.setAttribute("target", "_blank");
      link.setAttribute("rel", "noopener noreferrer"); // Seguridad adicional
    }
  });
}

