function quitarEspacios(){
  posts = document.querySelectorAll('.post-body');
  for (let i = 0; i < posts.length; i++) {
    posts[i].innerHTML = posts[i].innerHTML.replace(/&nbsp;/g, " ");
  }
}   
function scrollFecha(element) { 
  element.parentElement.parentElement.scrollIntoView({ behavior: 'smooth', block: 'start' });
} // scrollea hasta el padre, del padre, del elemento     
function agregarBotonDescarga(imagen) {
    if (!imagen.nextElementSibling || !imagen.nextElementSibling.classList.contains('download-btn')) {
      imagen.insertAdjacentHTML('beforebegin', `
        <button class="download-btn" onclick="
          event.preventDefault(); 
          event.stopPropagation();
          descargarImagen('${imagen.parentElement.href}')
        ">
          <svg  xmlns="http://www.w3.org/2000/svg"  width="24"  height="24"  viewBox="0 0 24 24"  fill="none"  stroke="#39a900"  stroke-width="2"  stroke-linecap="round"  stroke-linejoin="round"  class="icon icon-tabler icons-tabler-outline icon-tabler-photo-down"><path stroke="none" d="M0 0h24v24H0z" fill="none"/><path d="M15 8h.01" /><path d="M12.5 21h-6.5a3 3 0 0 1 -3 -3v-12a3 3 0 0 1 3 -3h12a3 3 0 0 1 3 3v6.5" /><path d="M3 16l5 -5c.928 -.893 2.072 -.893 3 0l4 4" /><path d="M14 14l1 -1c.653 -.629 1.413 -.815 2.13 -.559" /><path d="M19 16v6" /><path d="M22 19l-3 3l-3 -3" /></svg>
        </button>
      `);
    }
}   
// aplica la libreria Fancybox a las imagenes del blog y pone los botones de descarga
function customImagenesPosts() {
  posts.forEach((post, index) => {
      post.querySelectorAll("img").forEach(img => {
        img.parentElement.setAttribute("data-fancybox", "post-group-" + index);
        agregarBotonDescarga(img)
      });
  });
  Fancybox.bind("[data-fancybox]", {
    compact: true, //desactiva la vercion movil
    animated: true, //  animaciones
    panOnlyZoomed: true, // Permite hacer zoom incluso sin hacer clic
    on: {
      close: () => {
        // prevenir errores con lectores de pantalla
        if (document.activeElement) {
          document.activeElement.blur();
        }
      }
    }
  });
  document.documentElement.style.setProperty("--fancybox-zIndex", "18");
}
