// funcion para quitar overlays
function iframeOverlay(element){ 
  if (element.style.display != 'none') {
    element.style.display = 'none'
    document.addEventListener("pointerdown", function handleClick(event) {
      if (!element.parentElement.contains(event.target)) { // click fuera del contenedor
        element.style.display = "flex"; // mostrar
        document.removeEventListener("click", handleClick); // eliminar evento
      }
    });
  }
}

// FunciÃ³n para copiar texto al portapapeles
async function copiarTexto(texto) {
  try {
    await navigator.clipboard.writeText(texto);
    mostrarNotificacion("Texto copiado", "texto guardado en tu portapapeles", "positiva")
  } catch (error) {
    console.error("Error al copiar el texto: ", error);
    mostrarNotificacion("Error al copiar el texto: ", error, "negativa")
  }
} 

// FunciÃ³n para descargar una imagen de un dominio diferente 
async function descargarImagen(imageURL){
  mostrarNotificacion('descarga','preparando descarga','info')
  const proxyUrl = 'https://corsproxy.io/?';
  const url = new URL(imageURL);
  let fileName = decodeURIComponent(   // extraer nombre de la url
      url.pathname.split('/').pop()  
  ) || 'imagen';  // si no hay nombre
  try {
    // Usar proxy CORS para evitar problemas de polÃ­tica de same-origin
    const response = await fetch(proxyUrl + encodeURIComponent(imageURL));
    if (!response.ok) throw new Error(`Error HTTP: ${response.status}`);
    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = fileName;
    document.body.appendChild(link);
    link.click();
    // Limpieza despuÃ©s de 100ms para asegurar la descarga
    setTimeout(() => {
      window.URL.revokeObjectURL(url);
      document.body.removeChild(link);
    }, 100);
    mostrarNotificacion('descarga lista!','la imagen se descargara en breve','positiva')
  } catch (error) {
    console.error('Error:', error);
    mostrarNotificacion('descarga fallida',error.message,'negativa')
  }
}           

