div.script:
//js
                   let carpetas = {}
                   const contenedorRutas = document.getElementById('seccion-header-cont');
                   function asignarPaginaActual() {
                     const urlActual = window.location.pathname; 
                     const seccion_headers = contenedorRutas.querySelectorAll('.seccion-header'); 
                     seccion_headers.forEach(seccion_header => {
                       if (seccion_header.href) { // si es una pagina
                         const href = new URL(seccion_header.href).pathname;
                         if (urlActual === href) {
                           seccion_header.classList.add('pagina_actual');
                         }
                       } else { //si es un carpeta
                         const carpetaItems = seccion_header.querySelectorAll('.item-carpeta-header');
                         carpetaItems.forEach(carpetaItem => {
                           const href = new URL(carpetaItem.href).pathname;
                           if (urlActual === href) {
                             carpetaItem.classList.add('pagina_actual');
                             seccion_header.classList.add('pagina_actual');
                           }
                         })
                       }
                     })
                   }
                 // !js

b:if.script:
//js
                         var titulo = ''; // traer variables de blogger
                         var link = ''; 
                         // separar el titulo en dos variables ✂️👇
                         var carpetaTitulo = titulo.split('>')[0].trim() 
                         var carpetaItem = titulo.split('>')[1].trim()
                         if (!carpetas[carpetaTitulo]){ // ¿ya existe esta carpeta?
                           carpetas[carpetaTitulo] = [] // crear carpeta
                           // insertar carpeta vacia en el html 📁👇
                           contenedorRutas.insertAdjacentHTML("beforeend", `



                                 ${carpetaTitulo}












                           `);
                         };
                         carpetas[carpetaTitulo].push({ // crear nuevo item
                           "nombre": carpetaItem,
                           "link": link
                           })
                       //!js

