// funcion para quitar overlays
function iframeOverlay(element){ 
  if (element.style.display != 'none') {
    element.style.display = 'none'
    document.addEventListener("pointerdown", function handleClick(event) {
      if (!element.parentElement.contains(event.target)) { // click fuera del contenedor
        element.style.display = "flex"; // mostrar
        document.removeEventListener("click", handleClick); // eliminar evento
      }
    });
  }
}
// funcion para compartir
function compartir(link,texto = "") {
  if (navigator.share) {
    mostrarNotificacion("Porfavor", "Seleccione una aplicación para compartir ", "info")
    navigator.share({
      url:link,
      text: texto
    })
    .then(() => mostrarNotificacion("gracias por compartir 😁", "valoramos mucho la difucion del blog", "positiva"))
    .catch((error) => mostrarNotificacion("gracias por compartir 😁", "valoramos mucho la difucion del blog", "positiva"));
  } else {
    copiarTexto(link)
  }
}

// Función para copiar texto al portapapeles
async function copiarTexto(texto) {
  try {
    await navigator.clipboard.writeText(texto);
    mostrarNotificacion("Texto copiado", "guardado en tu portapapeles", "positiva")
  } catch (error) {
    mostrarNotificacion("Error al copiar el texto: ", error, "negativa")
  }
} 

// Función para descargar una imagen de un dominio diferente 
async function descargarImagen(imageURL){
  mostrarNotificacion('descarga','preparando descarga','info')
  const proxyUrl = 'https://corsproxy.io/?';
  const url = new URL(imageURL);
  let fileName = decodeURIComponent(   // extraer nombre de la url
      url.pathname.split('/').pop()  
  ) || 'imagen';  // si no hay nombre
  try {
    // Usar proxy CORS para evitar problemas de política de same-origin
    const response = await fetch(proxyUrl + encodeURIComponent(imageURL));
    if (!response.ok) throw new Error(`Error HTTP: ${response.status}`);
    const blob = await response.blob();
    const url = window.URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = fileName;
    document.body.appendChild(link);
    link.click();
    // Limpieza después de 100ms para asegurar la descarga
    setTimeout(() => {
      window.URL.revokeObjectURL(url);
      document.body.removeChild(link);
    }, 100);
    mostrarNotificacion('descarga lista!','la imagen se descargara en breve','positiva')
  } catch (error) {
    console.error('Error:', error);
    mostrarNotificacion('descarga fallida',error.message,'negativa')
  }
}     

function calcularTiempoTranscurrido(fechaTexto) {
  // Diccionario de meses en español
  const meses = {
    enero: 0, febrero: 1, marzo: 2, abril: 3,
    mayo: 4, junio: 5, julio: 6, agosto: 7,
    septiembre: 8, octubre: 9, noviembre: 10, diciembre: 11
  };

  // Extraer día, mes y año del texto
  const partes = fechaTexto.toLowerCase().split(" de ");
  const dia = parseInt(partes[0]);
  const mes = meses[partes[1]];
  const año = parseInt(partes[2]);

  // Crear objeto Date de la fecha dada y la fecha actual
  const fechaDada = new Date(año, mes, dia);
  const hoy = new Date();

  // Calcular diferencia en milisegundos y convertir a días
  const diferenciaMs = hoy - fechaDada;
  const dias = Math.floor(diferenciaMs / (1000 * 60 * 60 * 24));

  if (dias < 0) return "en el futuro";
  if (dias === 0) return "hoy";
  if (dias === 1) return "ayer";
  if (dias < 30) return `hace ${dias} días`;
  if (dias < 60) return "hace 1 mes";
  if (dias < 365) return `hace ${Math.floor(dias / 30)} meses`;
  if (dias < 730) return "hace 1 año";

  return `hace ${Math.floor(dias / 365)} años`;
}      

