function ajustarColumnas() {
  console.log("ajustarColumnas")
  const contenedor = document.getElementById('Blog1');
  const posts = contenedor.getElementsByClassName("post");
  for (let post of posts) {
    if (post.offsetHeight > 800) {
        post.classList.add("dos-columnas");
    }
  }
}
