const contenedor = document.getElementById("BlogSearch1_form");
const button = contenedor.querySelector('.buttonbuscador');
const input = contenedor.querySelector('.contenedorbuscador');

button.addEventListener('click', () => {
    if (button.classList.contains('lupagirando')) {
        return;
    }
    // A√±ade la clase para iniciar la animaci√≥n
    button.classList.add('lupagirando');
    input.classList.add('inputgolpe');

    // Elimina la clase despu√©s de que termine la animaci√≥n (3s)
    setTimeout(() => {
        button.classList.remove('lupagirando');
        input.classList.remove('inputgolpe');
    }, 2500);
});

const form = contenedor.querySelector('.gsc-search-box');
form.addEventListener('submit', (e) => {
    e.preventDefault();
        setTimeout(() => {
            form.submit();
        }, 2000); // Retraso de 2.1 segundos
});

