<!--* Elimina la clase 'loading' despu√©s de 10ms -->
window.setTimeout(function () {
  document.body.className = document.body.className.replace('loading', '');
}, 10);

// Funci√≥n para copiar texto al portapapeles
async function copiarTexto(texto) {
  try {
    await navigator.clipboard.writeText(texto);
    alert("Texto copiado: " + texto);
  } catch (error) {
    console.error("Error al copiar el texto: ", error);
    alert("No se pudo copiar el texto. Int√©ntalo de nuevo.");
  }
}

// eleva la galeria de imagenes
// asi el menu no las atrabieza
// con su "z-index" superior üëá

let elevacionfinalizada = false;
if (!elevacionfinalizada) {
  ajustarColumnas();
  const observer = new MutationObserver(() => {
    console.log("Mutation")
    const lastElement = document.body.lastElementChild;
    if (lastElement ? lastElement.classList.contains("CSS_LIGHTBOX") : false) {
      lastElement.style.zIndex = "10";
      elevacionfinalizada = true;
      observer.disconnect(); // Detiene el observer despu√©s de hacer el cambio
    }
  });

  observer.observe(document.body, {
    childList: true,
    subtree: false
  });
}

document.addEventListener("DOMContentLoaded", () => {
  asignarPaginaActual()
  ajustarColumnas()
});

