function ajustarColumnas() {
  console.log("ejecutado")
  const posts = document.querySelectorAll(".post");
  console.log(posts)
  posts.forEach(post => {
      if (post.offsetHeight > 800) {
        post.classList.add("dos-columnas");
      }
  });
}

window.addEventListener('DOMContentLoaded', function() {
  var parrafos = document.querySelectorAll('.post-body > p');
  parrafos.forEach(function(p) {
      // Usamos texto en Unicode y eliminamos todo tipo de espacios
      var contenido = p.textContent
          .replace(/\s+/g, '')       // Elimina espacios, tabs y saltos de línea
          .replace(/\u00a0/g, '')    // Elimina espacios no rompibles (nbsp;)
          .replace(/\u200B/g, '');   // Elimina espacios "zero-width" (caracteres invisibles)
      // Si no queda contenido, eliminamos el párrafo
      if (!contenido.length) {
          p.parentNode.removeChild(p); // Método compatible con navegadores antiguos
      }
  });
});

