const button = document.querySelector('.buttonbuscador');
const input = document.querySelector('.contenedorbuscador');
console.log(button);

button.addEventListener('click', () => {
    if (button.classList.contains('lupagirando')) {
        console.log('Ya se está animando');
        return;
    }
    console.log('click');
    // Añade la clase para iniciar la animación
    button.classList.add('lupagirando');
    input.classList.add('inputgolpe');

    // Elimina la clase después de que termine la animación (3s)
    setTimeout(() => {
        button.classList.remove('lupagirando');
        input.classList.remove('inputgolpe');
    }, 2500);
});

