let index = 0;
let isPaused = false;
carusel = document.getElementById("carusel-cont")


function actualizarIndexCarusel(contenedor = carusel) {
  let items = contenedor.querySelectorAll(".carusel-link");
  let scrollLeft = contenedor.scrollLeft;
  let closestIndex = 0;
  let minDiff = Math.abs(scrollLeft - items[0].offsetLeft);

  items.forEach((item, i) => {
    let diff = Math.abs(scrollLeft - item.offsetLeft);
    if (diff < minDiff) {
      minDiff = diff;
      closestIndex = i;
    }
  });
  index = closestIndex;
}

function moverCarusel(dirreccion = 'adelante', contenedor = carusel){
  let items = contenedor.querySelectorAll(".carusel-link");
  actualizarIndexCarusel(contenedor); // actualizar posicion encaso de scrolling
  if(dirreccion === "atras"){ 
    index-- // retroceder
    if (index < 0) {
        index = items.length - 1; // resetear al final
        contenedor.scrollTo({ // vuelve al inicio
          left: items[index].offsetLeft,
        });
        index--
    }
    contenedor.scrollTo({ //
        left: items[index].offsetLeft,
        behavior: "smooth"
    });
  } else {
    index++ // avanzar
    if (index >= items.length) { 
        index = 0; // resetear
        contenedor.scrollTo({ // vuelve al inicio
          left: items[index].offsetLeft,
        });
        index++ // sumar para moverse al index 2
    }  
    contenedor.scrollTo({ //
        left: items[index].offsetLeft,
        behavior: "smooth"
    });
  }
}

function autoScrollCarusel() {
  if (isPaused) return; // Detener el movimiento si est√° en hover
  moverCarusel()
}




document.addEventListener("DOMContentLoaded", function () {
  // Detectar hover para pausar el movimiento
  // Detectar hover para pausar el movimiento (PC)
  carusel.addEventListener("mouseenter", () => isPaused = true);
  carusel.addEventListener("mouseleave", () => isPaused = false);

  // Detectar toque en m√≥viles para pausar el movimiento
  carusel.addEventListener("touchstart", () => isPaused = true);
  document.addEventListener("visibilitychange", () => {
    isPaused = document.hidden;
  });

});
