  async function copiarTexto(texto) {
    try {
      await navigator.clipboard.writeText(texto);
      mostrarNotificacion("Texto copiado", "texto guardado en tu portapapeles", "positiva")
    } catch (error) {
      console.error("Error al copiar el texto: ", error);
      mostrarNotificacion("Error al copiar el texto: ", error, "negativa")
    }
  }

