      childList: true,
      subtree: false
    });
  }


function agregarBotonesDescarga() {
    // Para cada post, selecciona todas las im√°genes
    const images = document.querySelectorAll('.post-body img')
    images.forEach(img => {
      if (!img.nextElementSibling || !img.nextElementSibling.classList.contains('download-btn')) {
        img.insertAdjacentHTML('beforebegin', `
          <button class="download-btn" onclick="
            event.preventDefault(); 
            event.stopPropagation();
            descargarImagen('${img.src}')
          ">
            <svg  xmlns="http://www.w3.org/2000/svg"  width="24"  height="24"  viewBox="0 0 24 24"  fill="none"  stroke="#39a900"  stroke-width="2"  stroke-linecap="round"  stroke-linejoin="round"  class="icon icon-tabler icons-tabler-outline icon-tabler-photo-down"><path stroke="none" d="M0 0h24v24H0z" fill="none"/><path d="M15 8h.01" /><path d="M12.5 21h-6.5a3 3 0 0 1 -3 -3v-12a3 3 0 0 1 3 -3h12a3 3 0 0 1 3 3v6.5" /><path d="M3 16l5 -5c.928 -.893 2.072 -.893 3 0l4 4" /><path d="M14 14l1 -1c.653 -.629 1.413 -.815 2.13 -.559" /><path d="M19 16v6" /><path d="M22 19l-3 3l-3 -3" /></svg>
          </button>
        `);
      }
  });
}



  async function descargarImagen(imageURL){
    mostrarNotificacion('descarga','preparando descarga','info')
    const proxyUrl = 'https://corsproxy.io/?';
    const url = new URL(imageURL);
    let fileName = decodeURIComponent(   // extraer nombre de la url
        url.pathname.split('/').pop()  
    ) || 'imagen';  // si no hay nombre
    try {
      // Usar proxy CORS para evitar problemas de pol√≠tica de same-origin
      const response = await fetch(proxyUrl + encodeURIComponent(imageURL));
      if (!response.ok) throw new Error(`Error HTTP: ${response.status}`);
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = fileName;
      document.body.appendChild(link);
      link.click();
      // Limpieza despu√©s de 100ms para asegurar la descarga
      setTimeout(() => {
        window.URL.revokeObjectURL(url);
        document.body.removeChild(link);
      }, 100);
      mostrarNotificacion('descarga','descarga exitosa üòä','positiva')
    } catch (error) {
      console.error('Error:', error);
      mostrarNotificacion('descarga fallida',error.message,'negativa')
    }
  }



