b:includable.script:
// üëà ignorar simbolos como "&" "<>" !importante
//js üëà comando decorativo "Highlight String Code"
function asignarPaginaActual() {
  const urlActual = window.location.pathname;

  const contenedor = document.getElementById('seccion_header_cont');
  const seccion_headers = contenedor.querySelectorAll('.seccion_header');

  seccion_headers.forEach(seccion_header => {
    const href = new URL(seccion_header.href).pathname;
    if (urlActual === href) {
      seccion_header .classList.add('pagina_actual');
    }
  })
}
//!js
