function textoAnimado() {
  const contenedor = document.getElementById("texto-animado-cont");
  const textos = contenedor.getElementsByClassName("texto-animado");
  let textoCount = 0;
  contenedor.style.animation = "escribiryborrar 7s infinite, blinkCursor 1s infinite steps(1)";
  textos[textoCount].classList.add("txt-visible"); // Mostrar primer texto
  contenedor.addEventListener("animationiteration", (event) => {
    if (event.animationName !== "escribiryborrar") return;
    textos[textoCount].classList.remove("txt-visible"); // Ocultar texto actual
    textoCount++ // Avanzar contador
    if (textoCount == textos.length) textoCount = 0 // reset
    textos[textoCount].classList.add("txt-visible");// Mostrar nuevo texto
  });
};
