document.addEventListener("DOMContentLoaded", function () {
  const carousel = document.getElementById("carusel-cont");
  let index = 0;
  const items = document.querySelectorAll(".carusel-link");
  let isPaused = false;
  // Detectar hover para pausar el movimiento
  // Detectar hover para pausar el movimiento (PC)
  carousel.addEventListener("mouseenter", () => {
    isPaused = true;
  });
  carousel.addEventListener("mouseleave", () => {
    isPaused = false;
  });
  // Detectar toque en m√≥viles para pausar el movimiento
  carousel.addEventListener("touchstart", () => {
    isPaused = true;
  });
  document.addEventListener("visibilitychange", () => {
    isPaused = document.hidden;
  });
  function actualizarIndex() {
    let scrollLeft = carousel.scrollLeft;
    let closestIndex = 0;
    let minDiff = Math.abs(scrollLeft - items[0].offsetLeft);
    items.forEach((item, i) => {
      let diff = Math.abs(scrollLeft - item.offsetLeft);
      if (diff < minDiff) {
        minDiff = diff;
        closestIndex = i;
      }
    });
    index = closestIndex;
  }
  function moverCarousel() {
    if (isPaused) return; // Detener el movimiento si est√° en hover
    actualizarIndex();
    index++ // avanzar
    if (index >= items.length) { 
      index = 0; // resetear
      carousel.scrollTo({ // vuelve al inicio
        left: items[index].offsetLeft,
      });
      index++ // sumar para moverse al index 2
    }  
    carousel.scrollTo({ //
      left: items[index].offsetLeft,
      behavior: "smooth"
    });
  }
  setInterval(moverCarousel, 5000); // Mueve cada 5 segundos
});
