    async function copiarTexto(texto) {
      try {
        await navigator.clipboard.writeText(texto);
        alert("Texto copiado: " + texto);
      } catch (error) {
        console.error("Error al copiar el texto: ", error);
        alert("No se pudo copiar el texto. Int√©ntalo de nuevo.");
      }
    }

    // eleva la galeria de imagenes
    // asi el menu no las atrabieza
    // con su "z-index" superior üëá

    let elevacionfinalizada = false;
    if (!elevacionfinalizada) {
      const observer = new MutationObserver(() => {
        const lastElement = document.body.lastElementChild;
        if (lastElement ? lastElement.classList.contains("CSS_LIGHTBOX") : false) {
          lastElement.classList.add("destacado");
          elevacionfinalizada = true;
        }
      });

      observer.observe(document.body, {
        childList: true,
        subtree: false
      });
    }


  function agregarBotonesDescarga() {
  const posts = document.querySelectorAll('.post-body');

  posts.forEach(post => {
    // Para cada post, selecciona todas las im√°genes
    const images = post.querySelectorAll('img');

    images.forEach(img => {
      if (!img.nextElementSibling || !img.nextElementSibling.classList.contains('download-btn')) {
      const downloadBtn = document.createElement("a");
      downloadBtn.href = img.src;
      downloadBtn.download = "imagen";
      downloadBtn.innerHTML = `<svg xmlns="http://www.w3.org/2000/svg"
      height="24px" width="24px" fill="#39a900" viewBox="0 -960 960 960" >
      <path d="M480-320 280-520l56-58 104 104v-326h80v326l104-104 56 58-200
      200ZM240-160q-33 0-56.5-23.5T160-240v-120h80v120h480v-120h80v120q0
      33-23.5 56.5T720-160H240Z"/></svg>`;
      downloadBtn.className = "download-btn";
      img.insertAdjacentElement('afterend', downloadBtn);
      downloadBtn.addEventListener('click', function(e) {
        e.preventDefault();
        descargarImagen(img.src); //funci√≥n de descarga
      });
      }
    });
  });
}



    async function descargarImagen(imageURL){
      mostrarNotificacion('descarga','preparando descarga','info')
      const proxyUrl = 'https://corsproxy.io/?';
      const url = new URL(imageURL);
      let fileName = decodeURIComponent(   // extraer nombre de la url
          url.pathname.split('/').pop()  
      ) || 'imagen';  // si no hay nombre
      try {
          const response = await fetch(proxyUrl + decodeURIComponent(imageURL));
          const blob = await response.blob();
          const link = document.createElement('a');
          link.href = URL.createObjectURL(blob);
          link.download = fileName;
          link.click();
          URL.revokeObjectURL(link.href);
          mostrarNotificacion('descarga','descarga exitosa üòä','positiva')
      } catch (error) {
          console.error('Error:', error);
          mostrarNotificacion('descarga','descarga fallida','negativa')
      }
    }



    // apenas cargue la estructura de la pagina
